script:
//* TITLE Old Sidebar **//
//* VERSION 1.1.1 **//
//* DESCRIPTION Get the sidebar back **//
//* DEVELOPER estufar **//
//* FRAME false **//
//* BETA false **//

XKit.extensions.estufars_sidebar_fix = new Object({
	
	running: false,
	
	preferences: {
		"dashonly": {
			text: "Only run on the dashboard",
			default: false,
			value: false
		}
	},
	
	run: function() {
		XKit.tools.init_css("estufars_sidebar_fix");
		this.running = true;
		
		if (XKit.extensions.estufars_sidebar_fix.preferences.dashonly.value) {
			if (document.location.href.indexOf('://www.tumblr.com/dashboard') === -1) {
				return;
			}
		} else {
			var disallowedurls = ["://www.tumblr.com/explore", "://www.tumblr.com/search", "/reblog"];
			for (var i = 0; i < disallowedurls.length; i++) {
				if (document.location.href.indexOf(disallowedurls[i]) !== -1) {
					return;
				}
			}
		}
		
		var account = document.getElementById("account_button");
		account.click();
		var popover = document.getElementsByClassName("popover--account-popover")[0];
		var sidebar = document.getElementById("right_column");
		popover.childNodes[0].classList.add("estufars_sidebar_fix");
		sidebar.insertBefore(popover.childNodes[0], sidebar.firstChild);
		account.style.display = "none";
		// this needs to be delayed a second for some reason
		window.setTimeout(function() {
			document.querySelector(".tab_nav_account.active").click();
		}, 1000);
	},
	
	destroy: function() {
		XKit.tools.remove_css("estufars_sidebar_fix");
		this.running = false;
		
		var account = document.getElementById("account_button");
		var sidebar = document.getElementsByClassName("estufars_sidebar_fix")[0];
		account.style.display = "inline-block";
		var popover = document.getElementsByClassName("popover--account-popover")[0];
		popover.insertBefore(sidebar, popover.firstChild);
		account.click();
		popover.style.opacity = "0";
		popover.style.display = "block";
		account.click();
		window.setTimeout(function() {
			popover.style.opacity = "1";
		}, 500);
	}
	
});

css:
.right_column:after {
	background: none!important;
}

.right_column .popover_menu {
	width: 229px!important;
	box-shadow: none!important;
	background: transparent!important;
	z-index: 0;
	position: static;
}

.right_column .popover .popover_inner_list .selected:not(.popover_menu_sublist) {
	background-color: transparent!important;
}

.right_column .popover_inner {
	border-radius: 0!important;
	box-shadow: none!important;
	padding: 0!important;
	background: transparent!important;
}

.right_column .popover_header {
	border-radius: 0;
	background: transparent;
	color: rgba(255,255,255,.3);
	padding: 0 10px;
	border-bottom: 1px solid rgba(255,255,255,.13);
	font-size: 12px;
}

.right_column .popover_header:not(:first-child) {
	padding-top: 18px;
}

.right_column .popover_menu_item:hover {
	background-color: transparent;
}

.right_column .popover_inner_list>.popover_menu_item:hover, .right_column .popover_menu_list .popover_menu_sublist .blog-sub-nav-item:hover, .right_column .blog-list-item:hover {
	background-color: #3c4b61!important;
	background-color: rgba(255,255,255,.05)!important;
	box-shadow: none!important;
}

.right_column .blog-list-item:hover .blog-list-item-info-name {
	color: rgba(255,255,255,.5)!important;
}

.right_column .popover_menu_item a, .right_column .blog-sub-nav-item-label {
	color: rgba(255,255,255,.5)!important;
	padding-left: 0;
}

.right_column .popover_item_suffix, .right_column .blog-sub-nav-item-data, .right_column .blog-list-item-info-title {
	color: rgba(255,255,255,.3)!important;
}

.right_column .popover .selected:not(.popover_menu_sublist) {
	background-color: rgba(255,255,255,.08)!important;
	box-shadow: none!important;
}

.right_column .popover_menu_item_blog {
	padding: 0;
}

.right_column .blog-list-item {
	padding-left: 10px;
}

.right_column .blog-sub-nav-item {
	padding: 0!important;
}

.right_column .popover_menu_item, .right_column .blog-sub-nav-item {
	border-top: 1px solid rgba(255,255,255,.08);
}

.right_column .blog-sub-nav-item a {
	padding: 10px!important;
}

.right_column .popover_menu_item {
	padding: 0;
}

.right_column .popover_menu_item .popover_menu_item_anchor {
	display: block;
	padding: 10px;
}

.right_column .popover_menu_item_anchor_icon {
	padding-right: 10px;
}

.right_column .popover_menu_item_anchor, .right_column .blog-sub-nav-item-label {
	font-weight: bold;
}

.right_column .tx-scroll {
	border-radius: 0!important;
}

.right_column .popover_menu_item_blog_details {
	overflow: hidden;
}

.right_column .popover_menu+#dashboard_controls_open_blog {
	display: none;
}
