script:
//* TITLE Pokés **//
//* VERSION 0.5.0 **//
//* DESCRIPTION Gotta catch them all! **//
//* DETAILS Randomly spawns Pokémon on your dash for you to collect. **//
//* DEVELOPER new-xkit **//
//* FRAME false **//
//* BETA true **//
//* SLOW true **//

XKit.extensions.pokes = {
	running: false,
	pokedex_url: "https://new-xkit.github.io/XKit/Extensions/dist/page/pokedex.json",

	preferences: {
		"catch_backgrounds": {
			text: "Give Pokémon a background (for visibility)",
			default: false,
			value: false
		},
		"pokes_menu_header": {
			text: "Menu",
			type: "separator",
		},
	},

	run: function() {
		this.running = true;
		XKit.tools.init_css('pokes');
		XKit.post_listener.add('pokes', XKit.extensions.pokes.checkEligibility);
		XKit.extensions.pokes.checkEligibility();
	},

	fetch_pokedex: function(callback, error) {
		if (!XKit.extensions.pokes.gist_cache) {
			GM_xmlhttpRequest({
				method: "GET",
				url: XKit.extensions.pokes.pokedex_url,
				json: true,
				onerror: function(response) {
					console.log("Poke data could not be retrieved. Skipping instance.");
					if (error) { error(response); }
				},
				onload: function(response) {
					var mdata = {};
					try {
						mdata = JSON.parse(response.responseText);
						XKit.extensions.pokes.gist_cache = mdata;
						callback(mdata);
					} catch(e) {
						console.log("Poke data received was not valid JSON. Skipping instance.");
						if (error) { error(response); }
					}
				}
			});
		} else {
			callback(XKit.extensions.pokes.gist_cache);
		}
	},

	checkEligibility: function() {
		$(".post_avatar:not(.poked):not(.unpokable)").each(function() {
			if (XKit.extensions.pokes.chanceGen()) {
				$(this).addClass("poked");
			} else {
				$(this).addClass("unpokable");
			}
		});

		$(".poked:not(.poke_spawned)").each(function() {
			var pokeNr = XKit.extensions.pokes.pokeGen();
			XKit.extensions.pokes.fetchPoke(pokeNr, $(this));
			$(this).addClass("poke_spawned");
		});
	},

	parse_pokemon: function(mdata, db_nr, pokedThing) {
		var poke_name = mdata[db_nr].name;
		var poke_sprite = mdata[db_nr].sprite;
		var m_f_ratio = parseInt(mdata[db_nr].gender_rate);
		var rarity = parseInt(mdata[db_nr].rarity);

		var poke_gender = "undefined";
		if (m_f_ratio === -1) {
			poke_gender = "genderless";
		} else {
			var rnd_nr = Math.random();
			var male_ratio = (m_f_ratio / 8);
			if (rnd_nr <= male_ratio) {
				poke_gender = "female";
			} else {
				poke_gender = "male";
			}
		}

		var rarityPicker = Math.floor(Math.random() * 255);
		if (rarityPicker >= 0 && rarityPicker <= rarity) {
			var poke_html;
			if (XKit.extensions.pokes.preferences.catch_backgrounds.value) {
				poke_html = '<div class="poke poke_background" data-pokeid="'+db_nr+'" data-pokename="'+poke_name+'" data-pokegender="'+poke_gender+'">'+
				'<img src="'+poke_sprite+'" alt="'+poke_name+'"/>'+'</div>';
			} else {
				poke_html = '<div class="poke" data-pokeid="'+db_nr+'" data-pokename="'+poke_name+'" data-pokegender="'+poke_gender+'">'+
				'<img src="'+poke_sprite+'" alt="'+poke_name+'"/>'+'</div>';
			}
			pokedThing.after(poke_html);
			pokedThing.parent().find(".poke").click(function(event) {
				if (XKit.storage.get("pokes","pokemon_storage","") === "") {
					XKit.storage.set("pokes","pokemon_storage","[]");
				}

				try {
					var storage_array = JSON.parse(XKit.storage.get("pokes","pokemon_storage", ""));
					if (storage_array !== "") {
						var poke_id = $(this).data("pokeid");
						var poke_gender = $(this).data("pokegender");
						var poke_name = $(this).data("pokename");
						var poke_wiki_name = poke_name;

						if (poke_name.indexOf(" ") > -1) {
							var firstWord = poke_name.split(" ")[0];
						if (firstWord == "Mega" || firstWord == "Primal") {
								poke_wiki_name = poke_name.split(" ")[1];
							} else if (firstWord == "Cosplay") {
								poke_wiki_name = "Cosplay Pikachu";
							} else {
								poke_wiki_name = firstWord;
							}
						}

						var old_amount = 0;
						for (var i = 0; i < storage_array.length; i++) {
							if (storage_array[i].id === poke_id && storage_array[i].gender === poke_gender) {
								old_amount = storage_array[i].amount;
								storage_array.splice(i, 1);
							}
						}
						storage_array.push({id: poke_id, gender: poke_gender, amount: old_amount + 1});
						XKit.storage.set("pokes","pokemon_storage",JSON.stringify(storage_array));
						XKit.notifications.add("You caught a " + poke_gender + " " + poke_name.charAt(0).toUpperCase() + poke_name.substr(1) + "!","pokes", false, function () {
							window.open("http://bulbapedia.bulbagarden.net/wiki/" + poke_wiki_name);
						});
						$(this).hide();
					} else {
						XKit.window.show("Catching failed!", "Something went wrong trying to catch the Pokémon. Please try again.<br/><br/>Error code: PKMN-001","error","<div class=\"xkit-button default\" id=\"xkit-close-message\">OK</div>");
					}
				} catch(e) {
					XKit.window.show("Catching failed!", "Something went wrong trying to catch the Pokémon. Please try again.<br/><br/>Error code: PKMN-002","error","<div class=\"xkit-button default\" id=\"xkit-close-message\">OK</div>");
				}
			});
		} else {
			XKit.extensions.pokes.parse_pokemon(mdata, XKit.extensions.pokes.pokeGen(), pokedThing);
		}
	},

	fetchPoke: function(db_nr, pokedThing) {
		XKit.extensions.pokes.fetch_pokedex(function(mdata) {
			XKit.extensions.pokes.parse_pokemon(mdata, db_nr, pokedThing);
		});
	},

	chanceGen: function() {
		var rnd_nr = Math.floor(Math.random() * 101);
		if (rnd_nr >= 0 && rnd_nr <= 8) {
			return true;
		}
		else {
			return false;
		}
	},

	pokeGen: function() {
		return Math.floor(Math.random() * 889);
	},

	destroy: function() {
		this.running = false;
		XKit.post_listener.remove("pokes");
	},

	render_pokelist: function() {
		var m_html = 
			'<div class="xkit-pokes-lightbox" style="opacity: 0">' +
			'<div class="xkit-pokes-pc">' +
				'<div class="xkit-pokes-pc-info">' +
					'<div class="gender"></div>' +
					'<div class="nickname"></div>' +
					'<div class="species"></div>' +
					'<div class="caught_stats"></div>' +
				'</div>' +
				'<div class="xkit-pokes-pc-pokemon">' +
					'<div id="xkit-loading_pokemon">' +
						'Loading Pokémon, please wait...' +
					'</div>' +
				'</div>' +
			'</div>';

		$("body").prepend(m_html);
		$(".xkit-pokes-lightbox").click(function(e) {
			if (e.target !== this) return;
			$(this).animate({
				opacity: 0
			}, {
				complete: function() {
					$(this).remove();
				}
			});
		});
		$(".xkit-pokes-lightbox").animate({
			opacity: 1
		});
		XKit.extensions.pokes.fetch_pokedex(function(mdata) {
			var caught = JSON.parse(XKit.storage.get("pokes","pokemon_storage","[]"));
			var header = "<p>You've caught " + caught.length + " total Pokémon!<br/> That's ";
			var checklist = [];
			var m_html = "";
			$.each(caught, function(index, value) {
				m_html = m_html + "<div class='caught' data-pokegender='" + value.gender + "' data-pokespecies='" + mdata[value.id].name + "' data-pokenick='" + (value.nickname || "") + "'><img class='caught poke_sprite' src='" + mdata[value.id].sprite + "'></div>";
				if (checklist.indexOf(value.id) === -1) checklist.push(value.id);
			});
			header += checklist.length + " out of " + mdata.length + " different species of Pokémon!</p>";
			$("#xkit-loading_pokemon").html(m_html);
			$(".caught_stats").html(header);		
			$(".xkit-pokes-pc-pokemon .caught").click(function () {
				if ($(this).hasClass("active")) {
					$(this).removeClass("active");
					$(".xkit-pokes-pc-info .caught_stats").show();
					$(".xkit-pokes-pc-info .gender").hide();
					$(".xkit-pokes-pc-info .species").hide();
					$(".xkit-pokes-pc-info .nickname").hide();
				} else {
					$(".active").removeClass("active");
					$(this).addClass("active");
					$(this).find("img").addClass("active");
					$(".xkit-pokes-pc-info .caught_stats").hide();
					$(".xkit-pokes-pc-info .gender").show();
					$(".xkit-pokes-pc-info .species").show();
					$(".xkit-pokes-pc-info .nickname").show();
					$(".xkit-pokes-pc-info .gender").attr(  "data-pokegender", ( $(this).attr("data-pokegender") ) );
					$(".xkit-pokes-pc-info .gender").text( $(this).attr("data-pokegender") );
					$(".xkit-pokes-pc-info .species").text( "Species: " + $(this).attr("data-pokespecies") );
					if (typeof $(this).attr("data-pokenick") !== "undefined" && $(this).attr("data-pokenick")) {
						$(".xkit-pokes-pc-info .nickname").html("<div>" + $(this).attr("data-pokenick") + "</div>" );
					} else {
						$(".xkit-pokes-pc-info .nickname").html("<div title='Click to nickname!'>" + $(this).attr("data-pokespecies") + "</div>");
					}
				}
			});
		}, function(response) {
			$("#xkit-loading_pokemon").html("<div id='xkit-pokes-custom-panel'>Failed to load Pokémon Data!<br>Please refresh the page or try again later!</div>");
		});
	},

	cpanel: function(m_div) {
		var m_html = '<div id="xkit-pokes-menu">' +
			'<div class="xkit-pokes-menu_button" id="xkit-pokes-show_poke_list">' +
				'<div class="icon"></div>' +
				'<div class="title">Pokémon</div>' +
				'<div class="description">Check which Pokémon you have caught</div>' +
			'</div>' +
			'<div class="xkit-pokes-menu_button disabled">' +
				'<div class="icon"></div>' +
				'<div class="title"></div>' +
				'<div class="description"></div>' +
			'</div>' +
			'<div class="xkit-pokes-menu_button disabled">' +
				'<div class="icon"></div>' +
				'<div class="title"></div>' +
				'<div class="description"></div>' +
			'</div>' +
		'</div>';
		m_div.append(m_html);

		$("#xkit-pokes-show_poke_list").click(function() {
			XKit.extensions.xkit_preferences.close();
			setTimeout(XKit.extensions.pokes.render_pokelist, 250);
		});

		$("#xkit-extensions-panel-right").nanoScroller();
		$("#xkit-extensions-panel-right").nanoScroller({ scroll: 'top' });
	}
};

css:
/*
 * Style for Pokémon appearing on Dashboard
*/
.poke {
	position: absolute;
	right: -126px;
}
.poke_background {
	background: url(data:image/png;base64,) no-repeat;
    background-size: 100px 100px;
	image-rendering: pixelated;
}
.poke img {
	width: 100px;
	image-rendering: pixelated;
}
.poke::after {
	font-size: 26px;
	font-weight: bold;
	position: relative;
	left: -26px;
}
.poke[data-pokegender="male"]::after {
	content: "\2642";
	color: #6CA0DC;
}
.poke[data-pokegender="female"]::after {
	content: "\2640";
	color: #FF69B4;
}
.poke[data-pokegender="genderless"]::after {
	content: "\26AA";
	color: #9370DB;
}

/*
 * Menu of the Pokés extension
*/
#xkit-pokes-custom-panel {
	width: 100%
}
#xkit-loading_pokemon {
	text-align: center;
	position: relative;
}
.xkit-pokes-menu_button {
	box-sizing: border-box;
	width: 178px;
	height: 175px;
	float: left;
	display: inline-block;
	position: relative;
	padding: 70px 20px 20px 20px;
	text-align: center;
	cursor: pointer;
	border: 1px solid #D0D0D0;
}
.xkit-pokes-menu_button.disabled {
	cursor: default;
}
.xkit-pokes-menu_button.disabled .title:after {
	content: "Unavailable";
}
.xkit-pokes-menu_button.disabled .description:after {
	content: "This feature is not ready just yet!";
}
.xkit-pokes-menu_button .title {
	font-weight: bold;
	margin-top: 4px;
}
.xkit-pokes-menu_button .description {
	max-height: 60px;
	overflow: hidden;
	text-overflow: ellipsis;
}
.xkit-pokes-menu_button .icon {
	position: absolute;
	top: 20px;
	left: 50%;
	margin-left: -16px;
	border-radius: 5px;
	width: 32px;
	height: 32px;
	background-size: contain;
	background-image: url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAACgAAAAoCAMAAAC7IEhfAAAACVBMVEX///8iIiIiIiLMFIP7AAAAA3RSTlMAf4C/aSLHAAAAVElEQVR4Ae3KMQrAIBQE0Z/c/9BJIQwsrFPaOI1/5c3tcM9KGZkjc2SO9i7vAnOZY3f4/u0hDjkloCQQAOQjAvAIzN1lccBV3l3SlMyROVJHsNvRPjeEAcfzDmAhAAAAAElFTkSuQmCC');
}
#xkit-pokes-show_poke_list .icon {
	background-image: url('data:image/png;base64,');
}

/*
 * Pokémon list style 
*/
.xkit-pokes-lightbox {
	position: fixed;
	top: 0px;
	bottom: 0px;
	right: 0px;
	left: 0px; 
	z-index: 15000;
	overflow: hidden;
	background-color: rgba(25,25,25,.92);
}
.gender {
	display: none;
	float: right;
	font-size: 20px;
}
.nickname {
	display: none;
	font-weight: 700;
	font-size: 25px;
}
.nickname::before {
	content: "";
	font-family: "tumblr-icons";
	float: left;
	display: none;
}
.species {
	display: none;
	font-size: 16px;
	margin-top: 3px;
}
#xkit-pokes-nickinput {
	font-size: 20px;
}
.xkit-pokes-pc {
	max-width: 800px;
	border-radius: 40px;
	height: 600px;
	background: #EFEFE7;
	position: relative;
	margin: 50px auto;
}
.xkit-pokes-pc-pokemon {
	height: 350px;
	width:85%;
	padding: 10px;
	margin: 50px;
	position: absolute;
	border: 4px solid #6B94B5;
	border-radius: 20px;
	background: #B6E4EF;
}
.xkit-pokes-pc-info {
	position: absolute;
	background: #fff;		
	height: 90px;
	width: 85%;
	padding: 10px;
	margin: 440px 50px 50px 50px;
	border: 4px solid #6B94B5;
	border-radius: 20px;
	-webkit-filter: grayscale(30%);
	filter: grayscale(0%);
}
.xkit-pokes-pc-info .gender[data-pokegender="male"]::after {
	content: " \2642";
	color: #6CA0DC;
}
.xkit-pokes-pc-info .gender[data-pokegender="female"]::after {
	content: " \2640";
	color: #FF69B4;
}
.xkit-pokes-pc-info .gender[data-pokegender="genderless"]::after {
	content: " \26AA";
	color: #9370DB;
}
.xkit-pokes-pc-info .caught_stats {
	text-align: center;
	font-weight: bold;
	font-size: 20px;
}
.caught.active {
	background: rgba(255,255,255,.7);
}
#xkit-loading_pokemon {
	overflow-y:scroll;
	height: 100%;
}
.caught {
	display: inline-block;
	width: 90px;
	height: 90px;
	border-radius: 10px;
}
.caught img {
	width: 90px;
	height: auto;
	vertical-align:middle;
	image-rendering: pixelated;
}
.caught::after {
	float: right;
	font-size: 30px;
	position: relative;
	top: -40px;
	font-weight: 100;
}
.caught[data-pokegender="male"]::after {
	content: " \2642";
	color: #6CA0DC;
}
.caught[data-pokegender="female"]::after {
	content: " \2640";
	color: #FF69B4;
}
.caught[data-pokegender="genderless"]::after {
	content: " \26AA";
	color: #9370DB;
}
